import React from 'react';
import { Button } from 'antd';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';


const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: calc(100vh - 64px); // Adjusting height for the navbar
  background-color: #f5f5f5;
`;

const Title = styled.h1`
  color: #8b4513;
  font-size: 48px;
  margin-bottom: 20px;
`;

const Description = styled.p`
  font-size: 20px;
  color: #333;
  text-align: center;
  margin-bottom: 30px;
`;

const HomeButton = styled(Button)`
  background-color: #8b4513;
  border-color: #8b4513;
  color: #fff;

  &:hover {
    background-color: #a0522d;
    border-color: #a0522d;
  }
`;

const ExploreButton = styled(Button)`
  margin-top: 20px;
  background-color: #4caf50;
  border-color: #4caf50;
  color: #fff;

  &:hover {
    background-color: #45a049;
    border-color: #45a049;
  }
`;

const Home = () => {
  const navigate = useNavigate();

  const exploreScholarships = () => {
    navigate('/scholarships'); // Redirect to the Explore Scholarships page
  };

  return (
    <>
      <NavBar /> {/* Include the NavBar */}
      <HomeContainer>
        <Title>Welcome to the Scholarship Management System</Title>
        <Description>
          Here you can manage your scholarships, applications, and more.
        </Description>
        <ExploreButton onClick={exploreScholarships}>Explore Scholarships</ExploreButton>
      </HomeContainer>
    </>
  );
};

export default Home;
