import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';

const Background = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: black;
`;

const neonGlow = keyframes`
  0%, 100% {
    text-shadow: 0 0 20px #ff2d00, 0 0 30px #ff2d00, 0 0 40px #ff2d00, 0 0 50px #ff8c00, 0 0 60px #ff8c00, 0 0 70px #00ffea, 0 0 80px #00ffea;
    color: #ffffff;
  }
  50% {
    text-shadow: 0 0 30px #00ffea, 0 0 40px #00ffea, 0 0 50px #00ffea, 0 0 60px #ff2d00, 0 0 70px #ff8c00, 0 0 80px #ff8c00;
    color: #00ffea;
  }
`;

const letterAppear = keyframes`
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
`;

const StyledText = styled.h1`
  font-family: 'Cursive', 'Brush Script MT', cursive; /* Cursive font */
  font-size: 100px; /* Increased font size */
  color: white;
  text-shadow: 0 0 10px #ff2d00, 0 0 20px #ff2d00, 0 0 30px #ff2d00;
  letter-spacing: 3px;
  display: inline-block;
  animation: ${neonGlow} 1.5s infinite alternate;
`;

const Letter = styled.span`
  display: inline-block;
  opacity: 0;
  animation: ${letterAppear} 0.5s ease-in-out forwards;
  animation-delay: ${({ delay }) => delay}s;
`;

const WelcomeScreen = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/login'); // Redirect to the login page after animation
    }, 6000); // Adjust timing based on how long the animation should last

    return () => clearTimeout(timer); // Cleanup timeout on component unmount
  }, [navigate]);

  const welcomeText = "Welcome to AspireAide";

  return (
    <Background>
      <StyledText>
        {welcomeText.split('').map((letter, index) => (
          <Letter key={index} delay={index * 0.2}>
            {letter === ' ' ? '\u00A0' : letter} {/* Handle spaces */}
          </Letter>
        ))}
      </StyledText>
    </Background>
  );
};

export default WelcomeScreen;
