import React from 'react';
import { Button } from 'antd';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #f5f5f5;
`;

const Title = styled.h1`
  color: #8b4513;
  font-size: 48px;
  margin-bottom: 20px;
`;

const Description = styled.p`
  font-size: 20px;
  color: #333;
  text-align: center;
  margin-bottom: 30px;
`;

const HomeButton = styled(Button)`
  background-color: #8b4513;
  border-color: #8b4513;
  color: #fff;

  &:hover {
    background-color: #a0522d;
    border-color: #a0522d;
  }
`;

const ExploreButton = styled(Button)`
  margin-top: 20px;
  background-color: #4caf50;
  border-color: #4caf50;
  color: #fff;

  &:hover {
    background-color: #45a049;
    border-color: #45a049;
  }
`;

const Home = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Implement logout logic here
    alert("You have been logged out.");
    navigate('/login'); // Redirect to login page
  };

  const exploreScholarships = () => {
    // Redirect to the Explore Scholarships page
    navigate('/scholarships');
  };

  return (
    <>
      <NavBar>
        <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
      </NavBar>
      <HomeContainer>
        <Title>Welcome to the Scholarship Management System</Title>
        <Description>
          Here you can manage your scholarships, applications, and more.
        </Description>
        <ExploreButton onClick={exploreScholarships}>Explore Scholarships</ExploreButton>
      </HomeContainer>
    </>
  );
};

const NavBar = styled.div`
  position: absolute;
  top: 0;
  width: 100%;
  background-color: #8b4513;
  padding: 10px;
  display: flex;
  justify-content: flex-end;
`;

const LogoutButton = styled(Button)`
  background-color: #ff6347;
  border-color: #ff6347;
  color: #fff;

  &:hover {
    background-color: #ff4500;
    border-color: #ff4500;
  }
`;

export default Home;
