import React, { useState } from 'react';
import { Form, Input, Button, notification } from 'antd';
import { UserOutlined, LockOutlined, MailOutlined, NumberOutlined } from '@ant-design/icons';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const SignupContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: #f0f2f5;
`;

const SignupForm = styled(Form)`
  max-width: 400px;
  width: 100%;
  padding: 30px;
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
`;

const Signup = () => {
  const [otpSent, setOtpSent] = useState(false);
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();

  const openNotification = (type, message) => {
    notification[type]({
      message: 'Signup Attempt',
      description: message,
      placement: 'top',
      duration: 3,
    });
  };

  const onFinish = async (values) => {
    try {
      const response = await axios.post('http://localhost:8091/api/signup', values);
      setOtpSent(true);
      setEmail(values.email);
      openNotification('success', 'OTP sent to your email! Please verify.');
    } catch (error) {
      openNotification('error', 'Signup failed. Please try again.');
    }
  };

  const verifyOtp = async () => {
    try {
      const response = await axios.post(`http://localhost:8091/api/signup/verify?email=${email}&otp=${otp}`);
      openNotification('success', 'Signup successful! You can now log in.');
      navigate('/login');
    } catch (error) {
      openNotification('error', 'OTP verification failed. Please try again.');
    }
  };

  return (
    <SignupContainer>
      {otpSent ? (
        <SignupForm>
          <h2>Enter OTP</h2>
          <Form.Item
            name="otp"
            rules={[{ required: true, message: 'Please enter the OTP!' }]}
          >
            <Input prefix={<NumberOutlined />} placeholder="Enter OTP" onChange={(e) => setOtp(e.target.value)} />
          </Form.Item>
          <Form.Item>
            <Button type="primary" onClick={verifyOtp}>
              Verify OTP
            </Button>
          </Form.Item>
        </SignupForm>
      ) : (
        <SignupForm name="signup-form" onFinish={onFinish}>
          <h2>Sign Up</h2>
          <Form.Item name="name" rules={[{ required: true, message: 'Please input your name!' }]}>
            <Input prefix={<UserOutlined />} placeholder="Name" />
          </Form.Item>
          <Form.Item name="email" rules={[{ required: true, type: 'email', message: 'Please input a valid email!' }]}>
            <Input prefix={<MailOutlined />} placeholder="Email" />
          </Form.Item>
          <Form.Item name="password" rules={[{ required: true, message: 'Please input your password!' }]}>
            <Input.Password prefix={<LockOutlined />} placeholder="Password" />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              Sign Up
            </Button>
          </Form.Item>
          <Link to="/login">Already have an account? Log in</Link>
        </SignupForm>
      )}
    </SignupContainer>
  );
};

export default Signup;
