import React, { useState } from 'react';
import { Button, Form, Input, Tabs, notification } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const { TabPane } = Tabs;

const LoginPage = () => {
  const [userType, setUserType] = useState('student'); // Default to 'student'
  const navigate = useNavigate();

  const handleLogin = (values) => {
    if (userType === 'admin' && values.username === 'admin' && values.password === 'admin123') {
      notification.success({
        message: 'Admin Login Successful',
      });
      localStorage.setItem('isAuthenticated', true); // Store login state
      navigate('/admin-dashboard'); // Redirect to admin dashboard
    } else if (userType === 'student' && values.username === 'student' && values.password === 'student123') {
      notification.success({
        message: 'Student Login Successful',
      });
      localStorage.setItem('isAuthenticated', true); // Store login state
      navigate('/student-dashboard'); // Redirect to student dashboard
    } else {
      notification.error({
        message: `${userType.charAt(0).toUpperCase() + userType.slice(1)} Login Failed`,
        description: 'Incorrect username or password.',
      });
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Login to Scholarship Management System</h2>

      {/* Tabs for Admin and Student */}
      <Tabs defaultActiveKey="1" onChange={(key) => setUserType(key === '1' ? 'student' : 'admin')}>
        <TabPane tab="Student Login" key="1">
          {/* Student Login Form */}
          <LoginForm handleLogin={handleLogin} />
        </TabPane>
        <TabPane tab="Admin Login" key="2">
          {/* Admin Login Form */}
          <LoginForm handleLogin={handleLogin} />
        </TabPane>
      </Tabs>
    </div>
  );
};

// Login Form Component
const LoginForm = ({ handleLogin }) => {
  return (
    <Form name="login-form" onFinish={handleLogin} style={styles.form}>
      <Form.Item
        name="username"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input prefix={<UserOutlined />} placeholder="Username" />
      </Form.Item>
      <Form.Item
        name="password"
        rules={[{ required: true, message: 'Please input your password!' }]}
      >
        <Input.Password prefix={<LockOutlined />} placeholder="Password" />
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit" block>
          Login
        </Button>
      </Form.Item>
    </Form>
  );
};

// Styles for the login page
const styles = {
  container: {
    width: '400px',
    margin: '0 auto',
    padding: '50px 20px',
    borderRadius: '10px',
    backgroundColor: '#f5f5f5',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
  },
  title: {
    textAlign: 'center',
    marginBottom: '20px',
    fontSize: '24px',
  },
  form: {
    maxWidth: '300px',
    margin: '0 auto',
  },
};

export default LoginPage;
