import React from 'react';
import { Form, Input, Button, notification } from 'antd';
import { UserOutlined, LockOutlined, MailOutlined } from '@ant-design/icons';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const SignupContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: linear-gradient(to right, #e3f2fd, #bbdefb); /* Light blue gradient */
`;

const SignupForm = styled(Form)`
  max-width: 400px;
  width: 100%;
  padding: 30px;
  background: #d2b48c;
  border-radius: 16px;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
`;

const StyledInput = styled(Input)`
  height: 48px;
`;

const SignupButton = styled(Button)`
  width: 100%;
  background-color: #0056b3; /* Darker blue */
  border-color: #0056b3;
  color: #fff;

  &:hover {
    background-color: #004494;
    border-color: #004494;
  }
`;

const SignUpLink = styled(Link)`
  color: #0056b3;
  &:hover {
    color: #003d7a;
  }
`;

const BlockbusterText = styled.h2`
  text-align: center;
  color: #ffffff;
  margin-bottom: 16px;
  font-size: 36px;
  font-family: 'Georgia', serif;
`;

const Signup = () => {
  const navigate = useNavigate();

  const openNotification = (type, message) => {
    notification[type]({
      message: 'Signup Attempt',
      description: message,
      placement: 'top',
      duration: 3,
    });
  };

  const onFinish = async (values) => {
    try {
      const response = await axios.post('http://localhost:8091/api/signup', values);
      console.log(response);
      navigate('/login');
      openNotification('success', 'Signup successful! Please log in.');
    } catch (error) {
      console.error('Signup failed:', error.message);
      if (error.response) {
        openNotification('error', error.response.data.message);
      } else {
        openNotification('error', 'Signup failed. Please try again later.');
      }
    }
  };

  return (
    <SignupContainer>
      <SignupForm name="signup-form" onFinish={onFinish}>
        <BlockbusterText>Sign Up</BlockbusterText>
        <Form.Item
          name="name"
          rules={[{ required: true, message: 'Please input your name!' }]}
        >
          <StyledInput prefix={<UserOutlined />} placeholder="Name" />
        </Form.Item>
        <Form.Item
          name="email"
          rules={[{ required: true, type: 'email', message: 'Please input a valid email!' }]}
        >
          <StyledInput prefix={<MailOutlined />} placeholder="Email" />
        </Form.Item>
        <Form.Item
          name="password"
          rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <StyledInput.Password prefix={<LockOutlined />} placeholder="Password" />
        </Form.Item>
        <Form.Item>
          <SignupButton type="primary" htmlType="submit">
            Sign Up
          </SignupButton>
        </Form.Item>
        <Form.Item>
          <p>
            Already have an account? <SignUpLink to="/login">Click here to log in</SignUpLink>
          </p>
        </Form.Item>
      </SignupForm>
    </SignupContainer>
  );
};

export default Signup;
