import React from 'react';
import { Form, Input, Button, Checkbox, notification } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const LoginContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: linear-gradient(to right, #e3f2fd, #bbdefb); /* Light blue gradient */
`;

const LoginForm = styled(Form)`
  max-width: 400px;
  width: 100%;
  padding: 30px;
  background: #d2b48c;
  border-radius: 16px;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
`;

const CustomCheckbox = styled(Checkbox)`
  margin-left: 8px;
`;

const LoginButton = styled(Button)`
  position: relative;
  background-color: #0056b3; /* Darker blue */
  border-color: #0056b3;
  color: #fff;
  font-weight: bold;

  &:hover {
    background-color: #004494;
    border-color: #004494;
  }
`;

const SignUpLink = styled(Link)`
  color: #0056b3;
  &:hover {
    color: #003d7a;
  }
`;

const BlockbusterText = styled.h2`
  text-align: center;
  color: #ffffff;
  margin-bottom: 16px;
  font-size: 36px;
  font-family: 'Georgia', serif;
`;

const StyledInput = styled(Input)`
  height: 48px;
`;

const StyledPasswordInput = styled(Input.Password)`
  height: 48px;
`;

const Login = () => {
  const navigate = useNavigate();

  const openNotification = (type, message) => {
    notification[type]({
      message: 'Login Attempt',
      description: message,
      placement: 'top',
      duration: 3,
    });
  };

  const onFinish = async (values) => {
    try {
      const response = await axios.post('http://localhost:8091/api/login', values, { withCredentials: true });
      console.log(response);
      navigate('/home');
      openNotification('success', 'You have successfully logged in!');
    } catch (error) {
      console.error('Login failed:', error.message);
      if (error.response) {
        openNotification('error', 'Invalid credentials. Please try again.');
      } else {
        openNotification('error', 'Login failed. Please try again later.');
      }
    }
  };

  return (
    <LoginContainer>
      <LoginForm
        name="login-form"
        initialValues={{ remember: true }}
        onFinish={onFinish}
      >
        <BlockbusterText>Login to AspireAide</BlockbusterText>
        <Form.Item
          name="email"
          rules={[{ required: true, message: 'Please input your email!' }]}
        >
          <StyledInput
            prefix={<UserOutlined style={{ color: '#fff' }} />}
            placeholder="Email"
          />
        </Form.Item>
        <Form.Item
          name="password"
          rules={[{ required: true, message: 'Please input your password!' }]}
        >
          <StyledPasswordInput
            prefix={<LockOutlined style={{ color: '#fff' }} />}
            placeholder="Password"
          />
        </Form.Item>
        <Form.Item>
          <Form.Item name="remember" valuePropName="checked" noStyle>
            <CustomCheckbox>Remember me</CustomCheckbox>
          </Form.Item>
        </Form.Item>
        <Form.Item>
          <LoginButton type="primary" htmlType="submit" block>
            Log In
          </LoginButton>
        </Form.Item>
        <Form.Item>
          <p>
            Don't have an account? <SignUpLink to="/signup">Click here to sign up</SignUpLink>
          </p>
        </Form.Item>
      </LoginForm>
    </LoginContainer>
  );
};

export default Login;
